import {
  Directive,
  Component,
  Input,
  Output,
  EventEmitter,
  ContentChild
} from '@angular/core';

@Directive({
  selector: 'ch-panel-header'
})
export class PanelHeaderDirective {
}

@Component({
  selector: 'ch-panel',
  templateUrl: 'panel.component.html',
  styleUrls: ['panel.component.css']
})
export class PanelComponent {

  open = true;
  @Input() title = '';
  @Output() panelToggled = new EventEmitter<PanelComponent>();
  @ContentChild(PanelHeaderDirective) panelHeader?: PanelHeaderDirective;

  togglePanel() {
    this.open = !this.open;
    this.panelToggled.emit(this);
  }

  hasHeader() {
    return this.panelHeader !== undefined;
  }
}

import {Input, Directive, ViewContainerRef, TemplateRef} from '@angular/core';

@Directive({
  selector: '[chRepeater]'
})
export class RepeaterDirective {
  constructor(private container: ViewContainerRef,
              private template: TemplateRef<any>) {
  }
  @Input('chRepeater') set repeatIt(count: number) {
    this.container.clear();
    for (let i = 0; i < count; i++) {
      this.container.createEmbeddedView(this.template);
    }
  }
}

import {Input, ElementRef, Output, Directive, EventEmitter, OnChanges} from '@angular/core';

declare var jQuery: any;

@Directive({
  selector: '[chSlider]'
})
export class SliderDirective implements OnChanges {
  sliderRef: any;
  @Input() value?: number;
  @Output() valueChange = new EventEmitter();

  constructor(private elementRef: ElementRef) {
    this.sliderRef = jQuery(this.elementRef.nativeElement).slider({
      slide: (event: any, ui: any) => {
        // console.log(ui.value); // Einkommentierung um Werte in der Console zu loggen
        this.valueChange.emit(ui.value);
      }
    });
  }
  ngOnChanges() {
    this.sliderRef.slider('option', {value: this.value});
  }
}

